import React from 'react'
import styles from './button.module.css'
import cn from 'classnames'
import Link from 'next/link'


function BaseButton({children, className,full=false, ...props}) {

    
    return (
        
            <button type="button" className={cn(styles.button, className, full && styles.fullWidth)} {...props}>{children}</button>
        
    )
}
function LinkButton({href,children, className, ...props}) {

    
    return (
        
            <Link href={href}>
                <a {...props}>{children}</a>
            </Link>
        
    )
}
function Button({children, className,full=false, ...props}) {
    const button = href ? LinkButton : BaseButton
    
    return (
        
            <button  className={cn(styles.button, className, full && styles.fullWidth)} {...props}>{children}</button>
        
    )
}


export default Button
