import React from 'react'
import styles from './navigation.module.css'
import NavButton from './nav-button'
import * as ICON from '../components/icons'
import {MENU} from '../components/DATA/MENU'



function Navigation({flat=true,children,text,icon}) {

    
    return (
        <nav className={styles.nav}>
            {/* <NavButton  selected=""><Icons.ArrowBottom /><span>Bookmark</span></NavButton> */}
            {
                MENU.map((item)=> {
                    const showTitle= !flat && item.text.length >0
                    return (<NavButton key={item.key} notify={item.notify}>
                    {item.logo}
                    {showTitle && item.text}
                    </NavButton>)
                    
                })
            }
            
        </nav>
    )
}


export default Navigation
