import React, { memo } from 'react'
import styles from './index.module.css'
import formatDistanceToNowStrict from 'date-fns/formatDistanceToNowStrict'
import Photo from '../photo'
import * as ICON from '../icons'
import IconButton from '../icon-button'
import {useEffect,useState} from 'react'




function StructureTweet({name,mention,date,children,big}) {
    const [tampon,setTampon]=useState(true)
    const [newText,setNewText]=useState([])
    const untilNow = formatDistanceToNowStrict(date)
    
     useEffect(()=> {
        setTimeout(setTampon(false),1500)
        // setNewText([sessionStorage.getItem('TEXT')])
        var gelen=sessionStorage.getItem('TEXT')
        var text =[]

        gelen.foreach((e,i)=> {
            text[i]=e
        })
        console.log('TEXT', text);
        
        
        console.log('BAK',sessionStorage.getItem('TEXT') );
       
        console.log('New Text', newText);
         
     },[])
    return (!tampon && newText.map((tweet)=> {
        console.log('TWEET', tweet);
            return (
                <div className={styles.article} key={tweet.index}>
            <div className={styles.photoDiv}>
                <Photo big/>
            </div>

               

            {/* BAŞLA */}

            <div className={styles.body} key={tweet.index}>
                <header className={styles.header}>
                    <span className={styles.name}>{name}</span>  <span>@{mention} </span> • <span>{untilNow}</span> </header>
                <p className={styles.content}>{tweet}</p>
                <footer className={styles.footer}> 
                <div className={styles.buttonFooter}>
                    <IconButton className={styles.iconButton}>
                        <ICON.Reply/>
                        <span>5</span>
                    </IconButton>
                    </div>
                    
                    <div className={styles.buttonFooter}>
                    <IconButton className={styles.iconButton}>
                        <ICON.Retweet/>
                        <span>12</span>
                    </IconButton>
                    </div>

                    <div className={styles.buttonFooter}>
                    <IconButton className={styles.iconButton}>
                        <ICON.Like/>
                    </IconButton>
                    </div>
                    
                    <div className={styles.buttonFooter}>
                    <IconButton className={styles.iconButton}>
                        <ICON.Share/>
                    </IconButton>
                    </div>
                
                </footer>
            </div>

            {/* BİTİR */}

        </div>

             )
        }
        
        
        ))
}

export default StructureTweet
